import ValidateInput from "../../validation/ValidateInput";
//import pageImg from "../../images/page_img.png";
import { withRouter } from "react-router";
import { Component } from "react";
import { Card,  CardText, CardTitle } from 'reactstrap'; 
import Form from "./LoginForm";

class Login extends Component {
  // state variable with email and password attributes
  state = {
    Email: null,
    Password: null,
  };
  // set value to state variable
  changeHandler = (event) => {
    this.setState({
      [event.target.id]: event.target.value,
    });
  };
  // submit the form
  submitHandler = (event) => {
    event.preventDefault();
    ValidateInput(this.state.Email, this.props.history);
  };
  render() {
    return (
      <div className="row">
       {/*left half to display data*/}
       <div className="col s12 m6">
         {/* <div className="imgPadding">
           <img src={pageImg} alt="" className="imgSize" />
         </div> */}
       <div>
            <Card style={{borderRadius:'15%',borderColor:'rgb(255, 255, 255)',backgroundColor:'rgb(255, 255, 255)'}}>
                <CardText style={{fontSize:'50px',color:'#35A653',fontWeight:'bold'}}>MoneyView</CardText>
                    <CardText style={{fontSize:'30px',textAlign:'center',margin:'auto',paddingLeft:'10px',color:'black',display:'flex', justifyContent:'center', alignContent:'center'}}>
                        We believe that access to financial services is a basic right of all individuals.
                    </CardText>
            </Card>
        </div>
        </div> 
        {/* right half of the page to input credentials */}
        <div className="col s12 m6">
          <div className="container loginFormPadding">
            {/* form to take login input */}
            <Form
              changeHandler={this.changeHandler}
              submitHandler={this.submitHandler}
            />
          </div>
        </div>
        {/* display login failed message if incorrect login credentials is entered */}
      </div>
    );
  }
}

export default withRouter(Login);
